package comsirojiddinovgaybullo.core.di.componentsimport android.app.Applicationimport comsirojiddinovgaybullo.core.di.module.Data.UserData.RoomModuleimport comsirojiddinovgaybullo.core.di.module.app.AppModuleimport comsirojiddinovgaybullo.core.di.ui.ActivitiesModuleimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectionModuleimport dagger.android.AndroidInjectorimport dagger.android.DaggerApplicationimport javax.inject.Singleton@Singleton@Component(    modules = [        AndroidInjectionModule::class,        AppModule::class,        ActivitiesModule::class,        RoomModule::class    ])interface AppComponent : AndroidInjector<DaggerApplication> {    override fun inject(instance: DaggerApplication)    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: Application): Builder        fun build(): AppComponent    }}