package comsirojiddinovgaybullo.core.di.module.Data.UserDataimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabase@Database(entities = [NoteItem::class], version = 1, exportSchema = true)abstract class Db : RoomDatabase() {    abstract fun noteDao(): NoteDao    companion object{        private  var db_instance:Db?= null        fun getDbInstance(context: Context):Db{            if (db_instance == null){                db_instance= Room.databaseBuilder(                    context.applicationContext,                    Db::class.java,                    "user_db"                )                    .allowMainThreadQueries()                    .build()            }            return db_instance!!        }    }}