package comsirojiddinovgaybullo.core.di.module.Data.UserDataimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport androidx.room.Updateimport io.reactivex.rxjava3.core.Single@Daointerface NoteDao {    @Query("select * from note_table")    fun getAll(): List<NoteItem>    @Insert(entity = NoteItem::class, onConflict = OnConflictStrategy.REPLACE)    fun addNote(noteItem: NoteItem)    @Update(entity = NoteItem::class, onConflict = OnConflictStrategy.REPLACE)    fun updateNote(noteItem: NoteItem)    @Delete(entity = NoteItem::class)    fun deleteNote(noteItem: NoteItem)}